import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-TableComponent',
  templateUrl: './TableComponent.component.html',
  styleUrls: ['./TableComponent.component.css']

})
export class TableComponentComponent implements OnInit {

  pipes = ['', '', '', 'humanizeDate', 'statusFromBoolean'];

  header = ['id', 'title', 'url', 'created', 'status'];





  rows = [
    [1, 'Home', 'home', '2016-08-27T17:48:32', true],
    [2, 'About Us', 'about', '2016-08-28T08:42:09', true],
    [4, 'Contact Us', 'contact', '2016-08-28T13:28:18', false],
  ];


  /** 14.6 */

  name = Promise.resolve('Misk');
  friends= new Observable<any>( observer => observer.next(['a','b','c']));


  time = new Observable<string>(observer => {
    setInterval(() => observer.next(new Date().toString()), 1000);
  });



  /**14.8 */

  value:boolean=true

  /**14.8 */

  superDate: Date =  new Date();



  constructor() {}

  ngOnInit() {}
}
<h1>Section 14.5: Dynamic Pipe</h1>

<table>
  <thead>
    <td *ngFor="let head of header">{{ head }}</td>
  </thead>
  <tr *ngFor="let row of rows; let i = index">
    <td *ngFor="let column of row">{{ column.toString()| dynamic:pipes[i]}}</td>
  </tr>
</table>
<hr/>

<h1>Section 14.6: Unwrap async values with async pipe</h1>

<h5>{{name | async}}</h5>
<h5>{{time |  async| date:'mm:ss'}}</h5>

<ul>
  <li *ngFor="let item of friends |async"> {{item}}</li>
</ul>

<hr />
<h1>Section 14.7: Stateful Pipes</h1>
<p> {{ 1000 | countdown:1 }}</p>
<p>{{ 300 | countdown }}</p>



<hr />
<h1>Section 14.8: Creating Custom Pipe</h1>
<p>{{value | truthy:'enabled':'disabled' }}</p>


<hr />
<h1>Section 14.10: Extending an Existing Pipe</h1>
<p> If is date :  {{superDate | ifdate:'dd/MM/yyyy'}}</p>
