import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-chapter302',
  templateUrl: './chapter302.component.html',
  styleUrls: ['./chapter302.component.css']
})
export class Chapter302Component implements OnInit {

  dynamicForm = this.fb.group({
    lessons: this.fb.array([])
  });

  hobbiesArray = new FormArray([new FormControl('',Validators.required)]);

  count: number = 0;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {



  }

  addControl() {
    this.hobbiesArray?.push( new FormControl('', Validators.required));
    this.count = this.count + 1;
  }

  removeControl(index: number) {
    this.hobbiesArray?.removeAt(index);
    this.count = this.count - 1;
  }

  onSubmit() {

  }
}

<div class="container-fluid">

  <form [formGroup]="dynamicForm" (ngSubmit)="onSubmit()" class="form-inline">
    <ng-container *ngIf="this.hobbiesArray.controls.length>0">
      <label for="control" class="m-2">control</label>
      <input type="text" class="form-control m-2" id="inlineFormEmail">
      <button type="submit" class="btn btn-primary m-2">Submit</button>
    </ng-container>
  </form>

</div>
