import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-chapter302',
  templateUrl: './chapter302.component.html',
  styleUrls: ['./chapter302.component.css']
})
export class Chapter302Component implements OnInit {


  dynamicForm: FormGroup | undefined;
  hobbiesArray= new FormArray([new FormControl(`control0`, Validators.required)]);;
  count: number = 0;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {

    this.dynamicForm = this.fb.group({
      someControls: this.fb.array(this.hobbiesArray.controls)
    });

  }

  addControl() {
    this.hobbiesArray?.push(new FormControl('', Validators.required));
    this.count = this.count + 1;
  }

  removeControl(index: number) {
    this.hobbiesArray?.removeAt(index);
    this.count = this.count - 1;
  }

  onSubmit() {
    window.alert(this.dynamicForm?.controls['someControls'].value);
  }
}

<form  formGroupName="dynamicForm" (ngSubmit)="onSubmit()">


  <div  formArrayName="hobbiesArray" class="container" *ngIf="this.hobbiesArray.controls.length>0">

    <div class="col-sm-12">
      <div class="row" *ngFor="let control of this.hobbiesArray.controls; let i = index">
        <div class="col-12 form-inline" >

          <label for="{{i}}" class="form-label">Controle {{i}}</label>
          <input type="text" class="form-control" formControlName="{{i}}">
          <button class="btn btn-danger">Deletar</button>
          <button class="btn btn-success">Incluir</button>


        </div>
      </div>
    </div>

  </div>




</form>
