import { Component, OnInit, ViewChild, ViewChildren  } from '@angular/core';
import { EditDirective } from './directive/edit.directive';

@Component({
  selector: 'app-chapter21',
  templateUrl: './chapter21.component.html',
  styleUrls: ['./chapter21.component.css']
})
export class Chapter21Component implements OnInit {

  @ViewChildren(EditDirective) vc!: EditDirective ;

  hero: string | undefined;
  power: string | undefined;

  @ViewChild('elements')
  elements!:any
  e: any;

  constructor() {
  }


  Execute(click: any) {
    let event = click;
    let ele=this.elements;
    console.log(`Target : ${event.target.innerHTML}`);

    console.log(this.vc)
   // this.vc.Edit();
    this.vc.map(c => c.Edit())
  }


  ngOnInit() {
    console.log('ngOnInit');
  }

  ngAfterViewInit(): void {
    console.log('ngAfterViewInit');
  }

  ngOnDestroy(): void {
    setTimeout(()=>{console.log(`Destroy`)},3000);
    console.log('ngOnDestroy');
    //Called once, before the instance is destroyed.
    //Add 'implements OnDestroy' to the class.

  }


}
<div class="container-fluid">

  <div class="col-12">

    <div class="row">
      <p>NgModel</p>
      <div class="col-4">
        <label for="hero">Nome do heroi :
          <input [(ngModel)]="hero" placeholder="Hero name">
        </label>
      </div>
      <div class="col-4">
        <label for="power">Poder do heroi :
          <input [(ngModel)]="power" placeholder="Hero power">
        </label>
      </div>
      <div class="col-4">
        {{hero}} {{power}}
      </div>
    </div>


    <hr>

    <div class="row">
      <p>#Elements</p>
      <div class="col-3">
        <label for="ElmentoOne">ElmentoOne :
          <input #elements myEdit placeholder="ElmentoOne" type="text" value="ElmentoOne" id="El1">
        </label>
      </div>
      <div class="col-3">
        <label for="ElmentoTwo">ElmentoTwo :
          <input #elements myEdit placeholder="ElmentoTwo" type="text" value="ElmentoTwo" id="El2">
        </label>
      </div>
      <div class="col-3">
       <button (click)="Execute($event)" class="btn btn-success"> Event Target </button>
      </div>

      <div class="col-3">

      </div>
    </div>




  </div>






  <app-chapter211 [hero]="hero" [power]="power"></app-chapter211>
</div>
