import { Component, OnInit } from '@angular/core';
import { UserService } from './services/user.service';
import { User } from './model/User';
import { map, take } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-chapter17',
  templateUrl: './chapter17.component.html',
  styleUrls: ['./chapter17.component.css'],
})
export class Chapter17Component implements OnInit {
  users: User[]=[];

  constructor(private activatedRoute: ActivatedRoute) {}

  ngOnInit() {
    this.activatedRoute.data.subscribe((data) => {
      this.users = data["user"] as User[];
    });

  }
}
<h1>Chapter 17</h1>
<hr>
<ul *ngIf="this.users && this.users.length> 1">
  <li *ngFor="let item of this.users; let i=index">
   <a [routerLink]="['/chapter17',item.id]"> {{item.title}}></a>
  </li>
</ul>

<hr>
<h2>Children route</h2>
<router-outlet></router-outlet>
