import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-chapter302',
  templateUrl: './chapter302.component.html',
  styleUrls: ['./chapter302.component.css']
})
export class Chapter302Component implements OnInit {

  dynamicForm = this.fb.group({
    skills: this.fb.array([]),
    hobbies: new FormControl('',Validators.required)
  });

  get  hobbiesArray():FormArray{
    return this.dynamicForm.get('hobbies') as FormArray
  }

  count: number = 0;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {



  }

  addControl() {
    this.hobbiesArray?.push( new FormControl('', Validators.required));
    this.count = this.count + 1;
  }

  removeControl(index: number) {
    this.hobbiesArray?.removeAt(index);
    this.count = this.count - 1;
  }

  onSubmit() {

     let control = this.hobbiesArray.controls;
     console.log(control);
  }
}

<div class="container-fluid">
  <form [formGroup]="dynamicForm" (ngSubmit)="onSubmit()" class="form-inline" >
    <div class="row" *ngIf="this.hobbiesArray.controls.length>0">
     <ng-container *ngFor="let control of this.hobbiesArray.controls;let i=index;">
      <div class="col-2"><label for="control" class="m-2" >control</label></div>
      <div class="col-7"><input type="text" class="form-control m-2 col-4" id="inlineFormEmail" formControlName="{{i}}" ></div>
      <div class="col-3"><button  type="button" class="btn btn-danger m-2"  (click)="removeControl(i)" *ngIf="this.hobbiesArray.controls.length>1">&nbsp;- &nbsp;</button><button  type="button" class="btn btn-primary m-2" (click)="addControl()">&nbsp;+ &nbsp;</button></div>

     </ng-container>
      <input type="submit" value="Mostrar" class="btn btn-success"/>
    </div>
  </form>
</div>
