import * as $ from 'jquery'

@Component({
  selector: 'app-chapter20',
  templateUrl: './chapter20.component.html',
  styleUrls: ['./chapter20.component.css']
})
export class Chapter20Component implements OnInit {

  constructor() { }

  ngOnInit() {
    $('#content').on('click', (p) => {
      window.alert(`Test ${$(p).val()}`);
    });
  }

}
