import { Component, Input, OnChanges, OnInit, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-chapter21',
  templateUrl: './chapter21.component.html',
  styleUrls: ['./chapter21.component.css']
})
export class Chapter21Component implements OnInit,OnChanges {

  @Input() hero: string | undefined;
  @Input() power: string | undefined;
  constructor() {
  }


  ngOnChanges(changes: SimpleChanges): void {

    for (let propName in changes) {
      let chng = changes[propName];
      let cur = JSON.stringify(chng.currentValue);
      let prev = JSON.stringify(chng.previousValue);
      console.log(`${propName}: currentValue = ${cur}, previousValue = ${prev}`);
    }
  }

  ngOnInit() {
    console.log('ngOnInit');
  }

  ngAfterViewInit(): void {
    console.log('ngAfterViewInit');
  }

  ngOnDestroy(): void {

    setTimeout(()=>{console.log(`Destroy`)},3000);
    console.log('ngOnDestroy');
    //Called once, before the instance is destroyed.
    //Add 'implements OnDestroy' to the class.

  }


}
