import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-chapter2',
  templateUrl: './chapter2.component.html',
  styleUrls: ['./chapter2.component.css']
})
export class Chapter2Component implements OnInit {


  profileForm!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {

     this.profileForm = this.fb.group({
      firstName: ['', Validators.required],
      personType: [''],

      aliases: this.fb.array([
        this.fb.control('')
      ])
    });

  }

  get aliases() {
    return this.profileForm.get('aliases') as FormArray;
  }

  addAlias() {
    this.aliases.push(this.fb.control(''));
  }

  onSubmit() {
    console.log(this.profileForm.value);
  }

}
<form [formGroup]="profileForm">

  <div class="form-group">
    <label for="firstName">firstName</label>
    <input type="text" class="form-control" name="firstName" id="firstName" formControlName="firstName">
  </div>

  <div class="form-group">
    <label for="personType">personType</label>
    <select class="form-control" name="personType" id="tipo" formControlName="personType">
      <option value="1">Pessoa física</option>
      <option value="2">Pessoa jurídica</option>
    </select>
  </div>



  <input type="submit" class="btn btn-primary mt-4" value="Salvar">



  {{this.profileForm.value}}
</form>
