import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[edit]'
})
export class EditDirective {

  constructor(public el: ElementRef) {
    this.el.nativeElement;
  }

  Edit() {
    var el = this.el.nativeElement;
    this.el.nativeElement.contentEditable = 'true';
  }

}
