import { Component, OnInit } from '@angular/core';
import { UserService } from './services/user.service';
import { User } from './model/User';
import { map, take } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-chapter17',
  templateUrl: './chapter17.component.html',
  styleUrls: ['./chapter17.component.css'],
})
export class Chapter17Component implements OnInit {

  users: User[]=[];

  constructor(private activatedRoute: ActivatedRoute) {}

  ngOnInit() {
    this.activatedRoute.data.subscribe((data) => {
      this.users = data["user"] as User[];
    });

  }
}
<h1>Chapter 17</h1>
<hr>

<div class="container-fluid">

  <div class="container">


    <div class="row">

      <div class="col-7">
        <ul *ngIf="this.users && this.users.length> 1; else oneUser">
          <li *ngFor="let item of this.users; let i=index">
            <a [routerLink]="['_171',item.id]"> {{item.title}}></a>
          </li>
        </ul>

        <ng-template #oneUser>
          {{this.users|json}}
        </ng-template>

      </div>
      <div class="col-5">

        <h2>Simple children route</h2>
        <router-outlet></router-outlet>

      </div>
    </div>






  </div>
</div>
