import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-chapter302',
  templateUrl: './chapter302.component.html',
  styleUrls: ['./chapter302.component.css']
})
export class Chapter302Component implements OnInit {


  dynamicForm: FormGroup | undefined;
  hobbiesArray: FormArray = new FormArray([new FormControl(`control0`, Validators.required)]);;
  count: number = 0;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {

    this.hobbiesArray = new FormArray([new FormControl(`control${this.count}`, Validators.required)]);


  }

  addControl() {
    this.hobbiesArray?.push(new FormControl(`control${this.count}`, Validators.required));
    this.count = this.count + 1;


  }

  removeControl(index: number) {
    this.hobbiesArray?.removeAt(index);
    this.count = this.count - 1;
  }

  onSubmit() {
    window.alert(this.hobbiesArray?.value);
  }
}

<form (ngSubmit)="onSubmit()">

  <div class="col-10" *ngIf="this.hobbiesArray?.controls?.length>0">


    <label for="exampleFormControlInput1" class="form-label">Email address</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" formControlName="">
  </div>

</form>
