import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-chapter2',
  templateUrl: './chapter2.component.html',
  styleUrls: ['./chapter2.component.css']
})
export class Chapter2Component implements OnInit {


  profileForm!: FormGroup;


  persons  =[{type:'F',name:"Fisica"},{type:"J",name:"Juridica"}];

  constructor(private fb: FormBuilder) { }

  ngOnInit() {

     this.profileForm = this.fb.group({
      firstName: ['', Validators.required],
      personType: [''],
      address: this.fb.group({
         street: [''],
         city: [''],
         state: [''],
         zip: ['']
       }),


      aliases: this.fb.array([
        this.fb.control('')
      ])
    });

  }

  get aliases() {
    return this.profileForm.get('aliases') as FormArray;
  }

  addAlias() {
    this.aliases.push(this.fb.control(''));


  }

  onSubmit() {
    console.log(this.profileForm.value);
  }

}
<form [formGroup]="profileForm" (ngSubmit)="onSubmit()">

  <div class="form-group">
    <label for="firstName">firstName</label>
    <input type="text" class="form-control" name="firstName" id="firstName" formControlName="firstName">
  </div>

  <div class="form-group">
    <label for="personType">personType</label>
    <select class="form-control" name="personType" id="personType" formControlName="personType">
      <option *ngFor="let person of this.persons" [ngValue]="person.type">{{person.name}}</option>
    </select>
  </div>

  <div formGroupName="address">
  <div class="form-group">
    <label for="street">street</label>
    <input type="text" class="form-control" name="street" id="street" formControlName="street">
  </div>
  </div>

<div formArrayName="aliases">
  <h2>Aliases</h2>
  <button type="button" (click)="addAlias()">+ Add another alias</button>

  <div *ngFor="let alias of aliases.controls; let i=index">
    <!-- The repeated alias template -->
    <label for="alias-{{ i }}">Alias:</label>
    <input id="alias-{{ i }}" type="text" [formControlName]="i">
  </div>
</div>


  <input type="submit" class="btn btn-primary mt-4" value="Salvar">




</form>
