import { Component, Input, OnInit, TrackByFunction } from '@angular/core';
import { Hero } from '../hero';

@Component({
  selector: 'app-chapter12',
  templateUrl: './chapter12.component.html',
  styleUrls: ['./chapter12.component.css']
})
export class Chapter12Component implements OnInit {

  @Input()
  hero: Hero | undefined;

  heroes = new Array<Hero>({ id: 1, name: "Adilson" }, { id: 1, name: "Luciana" }, { id: 3, name: "Vanessa" })

  constructor() { }

  ngOnInit() {
  }


  trackByFn(content: any) {
    console.log(JSON.parse(content));

  }

}
<div class="container">
  <div class="container">


    <li *ngFor="let item of heroes; let i = index; trackBy: trackByFn">
      {{i}} - {{item.name}}
    </li>


    <app-chapter121 *ngFor="let hero of heroes" [hero]="hero"></app-chapter121>


    <app-chapter124></app-chapter124>


  </div>
</div>
