import { User } from './../chapter17/model/User';
import { Component, OnInit } from '@angular/core';
import { UserToken } from './model/UserToken';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-chapter18',
  templateUrl: './chapter18.component.html',
  styleUrls: ['./chapter18.component.css']
})
export class Chapter18Component implements OnInit {

   userCharpter18!: UserToken;


  constructor(private activatedRoute: ActivatedRoute) { }

  ngOnInit() {




    this.activatedRoute.data.subscribe( (data)=>{
      this.userCharpter18 = data["currentUser"] as UserToken
    }
    );

  }

}
<h1>Chapter 18</h1>
<hr>

<div class="container-fluid">

  <div class="container">


    <div class="row">

      <div class="col-7">
        <div class="card" style="width: 18rem;">
          <div class="card-body">
            <h5 class="card-title">Card Title</h5>
            <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6>
            <p class="card-text">
              Usuario Id é {{this.userCharpter18.id}} e  nome : {{this.userCharpter18.name}}
            </p>
            <a [routerLink]="['chapter17','animal']" class="card-link"> animal </a>
            <a [routerLink]="['chapter16','fruit']"class="card-link"> fruit</a>
          </div>
        </div>


      </div>

      <div class="col-5">
        <router-outlet></router-outlet>
      </div>

    </div>
  </div>
</div>
