import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormArray, FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-chapter303',
  templateUrl: './chapter303.component.html',
  styleUrls: ['./chapter303.component.css']
})
export class Chapter303Component implements OnInit {

  @ViewChild('control') control!:ElementRef;

  profileForm = new FormGroup({
    firstName: new FormControl(''),
    lastName: new FormControl(''),
  });

  dynamicForm = new FormArray([new FormControl()]);
  constructor() { }

  ngOnInit() {



  }




  addControl(controlName:any='') {

    let control = new FormControl(controlName,Validators.required);



    this.dynamicForm.push(control);
  }

  removeControl(index: number) {
    this.dynamicForm.push(new FormControl(''));
  }

  onSubmit() {


    for (let index = 0; index < this.dynamicForm.controls.length; index++) {
       let element = ((this.dynamicForm.controls.at(index)) as FormControl);
      const txt = element.get(`control_${index}`) ;
       console.log(txt);
    }


  }

}
<div class="container-fluid">

    <div class="row" *ngIf="this.dynamicForm.controls.length>0" >

      <ng-container *ngFor="let ski of this.dynamicForm.controls;let i=index; let last=last" >
        <div class="col-2"><label for="name" class="m-2">control_{{i}}</label></div>
        <div class="col-7"><input type="text" class="form-control m-2 col-4" id="control_{{i}}" name="control_{{i}}" #control  [formControl]="ski">
        </div>
        <div class="col-3"><button type="button" class="btn btn-danger m-2" (click)="removeControl(i)"
            *ngIf="this.dynamicForm.controls.length>1">&nbsp;- &nbsp;</button><button type="button"
            class="btn btn-primary m-2" (click)="addControl()" *ngIf="last">&nbsp;+ &nbsp;</button></div>
      </ng-container>

      <input type="button" value="Mostrar" class="btn btn-success"  (click)="onSubmit()"/>
    </div>

</div>
