import { Component, Input, OnChanges, OnInit, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-chapter21',
  templateUrl: './chapter21.component.html',
  styleUrls: ['./chapter21.component.css']
})
export class Chapter21Component implements OnInit {

  hero: string | undefined;
  power: string | undefined;

  @Input()
  elements!: string[];

  constructor() {
  }

  ngOnInit() {
    console.log('ngOnInit');
  }

  ngAfterViewInit(): void {
    console.log('ngAfterViewInit');
  }

  ngOnDestroy(): void {
    setTimeout(()=>{console.log(`Destroy`)},3000);
    console.log('ngOnDestroy');
    //Called once, before the instance is destroyed.
    //Add 'implements OnDestroy' to the class.

  }


}
<div class="container-fluid">

  <div class="col-12">

    <div class="row">
      <p>NgModel</p>
      <div class="col-6">
        <label for="hero">Nome do heroi :
             <input [(ngModel)]="hero" placeholder="Hero name">
        </label>
      </div>
      <div class="col-6">
        <label for="power">Poder do heroi :
        <input [(ngModel)]="power" placeholder="Hero power">
        </label>
      </div>

      <div class="">

      </div>
    </div>
    <div class="row">

      <div class="col-6">
            Digitadado : {{hero}} {{power}}
      </div>
    </div>




  </div>

  <input #elements placeholder="ElmentoOne" type="text" value="ElmentoOne">
  <input #elements placeholder="ElmentoTwo" type="text" value="ElmentoTwo">



  <app-chapter211 [hero]="hero" [power]="power"></app-chapter211>
</div>
